import unittest

from utils import (generate_linear_peptides, generate_looped_peptides,
                   generate_peptide_pdb)


class TestUtilMethods(unittest.TestCase):
    def test_generate_linear_peptides(self):
        pdb_seq = ""
        linear_peptides = generate_linear_peptides(pdb_seq)
        self.assertGreater(len(linear_peptides), 0)

    def test_generate_looped_peptides(self):
        pdb_seq = ""
        looped_peptides = generate_looped_peptides(pdb_seq)
        self.assertGreater(len(looped_peptides), 0)

    def test_generate_peptide_pdb(self):
        peptide = 'ATRRYYLGAVELSWDYMQSD'
        generate_peptide_pdb(peptide)


if __name__ == '__main__':
    unittest.main()
